import { useEffect, useState } from "react"
import './EventSelection.css'

const EventSelection = () => {
  const [data, setData] = useState<any>();
  const [selectedType, setSelectedType] = useState("hyperglycemic");
  const [visibleEvents, setVisibleEvents] = useState<any>(data[selectedType]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("http://localhost:8000/interesting-events");
      const data = await response.json();
      setData(data);
    };
    fetchData();
  }, []);

  useEffect(() => {
    setVisibleEvents(data[selectedType]);
  }, [selectedType, data]);

  const handleRemoveEvent = (rangeIdx: number, eventId: number) => {
    const updatedEvents = visibleEvents.map((range: any, idx: number) => {
      if (idx === rangeIdx) {
        return {
          ...range,
          events: range.events.filter((event: any) => event.id !== eventId)
        };
      }
      return range;
    });

    setVisibleEvents(updatedEvents);
    setData({ ...data, [selectedType]: updatedEvents });
  };

  return (
    <div className="event-selection-container">
      {/* Event type switcher */}
      <div className="button-group">
        <button
          className={`event-button ${selectedType === "hyperglycemic" ? "active" : ""}`}
          onClick={() => setSelectedType("hyperglycemic")}
        >
          Hyperglycemic
        </button>
        <button
          className={`event-button ${selectedType === "hypoglycemic" ? "active" : ""}`}
          onClick={() => setSelectedType("hypoglycemic")}
        >
          Hypoglycemic
        </button>
      </div>

      {/* Event ranges and events */}
      {visibleEvents.map((range: any, rangeIdx: number) => (
        <div key={rangeIdx} className="event-range">
          <div className="event-range-header">
            Range: {new Date(range.range[0]).toLocaleString()} - {new Date(range.range[1]).toLocaleString()}
          </div>
          <ul className="event-list">
            {range.events.length > 0 ? (
              range.events.map((event: any) => (
                <li key={event.id} className="event-item">
                  <div className="event-details">
                    <strong>{event.type}</strong>: {event.description} at {new Date(event.timestamp).toLocaleString()}
                  </div>
                  <button
                    className="remove-event-button"
                    onClick={() => handleRemoveEvent(rangeIdx, event.id)}
                  >
                    X
                  </button>
                </li>
              ))
            ) : (
              <li className="no-events">No events</li>
            )}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default EventSelection;
