import React, { useState, useRef } from 'react';
import {
  Camera,
  Upload,
  X,
  Check,
  Loader,
  ThumbsUp,
  ThumbsDown,
  Save,
  PlusCircle,
  MinusCircle,
} from 'lucide-react';

interface Nutrition {
  servingSize: string;
  calories: number;
  carbohydrates: number;
  fat: number;
  protein: number;
}

interface Results {
  ingredients: string[];
  confidence: number;
  nutrition: Nutrition;
}

const PhotoCaptureComponent: React.FC = () => {
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [isProcessing, setIsProcessing] = useState<boolean>(false);
  const [results, setResults] = useState<Results | null>(null);
  const [feedback, setFeedback] = useState<boolean | null>(null);
  const [isEditing, setIsEditing] = useState<boolean>(false);
  const [editedResults, setEditedResults] = useState<Results | null>(null);
  const fileInputRef = useRef<HTMLInputElement | null>(null);

  const capturePhoto = (e: React.ChangeEvent<HTMLInputElement>): void => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => setCapturedImage(e.target?.result as string);
      reader.readAsDataURL(file);
    }
  };

  const processPhoto = async (): Promise<void> => {
    setIsProcessing(true);
    // Simulating API call for image processing
    await new Promise((resolve) => setTimeout(resolve, 2000));
    setResults({
      ingredients: ['Chicken', 'Lettuce', 'Tomato', 'Bread'],
      confidence: 0.85,
      nutrition: {
        servingSize: '1 sandwich (250g)',
        calories: 350,
        carbohydrates: 30,
        fat: 12,
        protein: 25,
      },
    });
    setIsProcessing(false);
  };

  const resetCapture = (): void => {
    setCapturedImage(null);
    setResults(null);
    setFeedback(null);
    setIsEditing(false);
    setEditedResults(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleFeedback = (isPositive: boolean): void => {
    setFeedback(isPositive);
    if (!isPositive && results) {
      setIsEditing(true);
      setEditedResults(JSON.parse(JSON.stringify(results))); // Deep copy
    }
  };

  const handleIngredientChange = (index: number, value: string): void => {
    if (editedResults) {
      const newIngredients = [...editedResults.ingredients];
      newIngredients[index] = value;
      setEditedResults({ ...editedResults, ingredients: newIngredients });
    }
  };

  const handleAddIngredient = (): void => {
    if (editedResults) {
      setEditedResults({
        ...editedResults,
        ingredients: [...editedResults.ingredients, ''],
      });
    }
  };

  const handleRemoveIngredient = (index: number): void => {
    if (editedResults) {
      const newIngredients = editedResults.ingredients.filter(
        (_, i) => i !== index
      );
      setEditedResults({ ...editedResults, ingredients: newIngredients });
    }
  };

  const handleNutritionChange = (
    key: keyof Nutrition,
    value: number | string
  ): void => {
    if (editedResults) {
      setEditedResults({
        ...editedResults,
        nutrition: { ...editedResults.nutrition, [key]: value },
      });
    }
  };

  const handleSaveEdits = (): void => {
    if (editedResults) {
      setResults(editedResults);
      setIsEditing(false);
      setFeedback(true);
    }
  };

  const NutritionItem: React.FC<{
    label: string;
    value: number | string;
    unit: string;
    editable: boolean;
    onChange: (value: number | string) => void;
  }> = ({ label, value, unit, editable, onChange }) => (
    <div className="flex justify-between items-center py-2 border-b border-gray-200 last:border-b-0">
      <span className="font-medium">{label}</span>
      {editable ? (
        <input
          type="number"
          value={value}
          onChange={(e) => onChange(e.target.value)}
          className="w-20 text-right border rounded px-1"
        />
      ) : (
        <span>{value}</span>
      )}
      <span className="ml-1">{unit}</span>
    </div>
  );

  return (
    <div className="max-w-md mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Analyze Your Food</h2>

      {!capturedImage && (
        <div className="space-y-4">
          <button
            onClick={() => fileInputRef.current?.click()}
            className="w-full bg-blue-500 text-white p-3 rounded-lg flex items-center justify-center"
          >
            <Camera className="mr-2" />
            Take Photo
          </button>
          <button
            onClick={() => fileInputRef.current?.click()}
            className="w-full bg-gray-200 text-gray-800 p-3 rounded-lg flex items-center justify-center"
          >
            <Upload className="mr-2" />
            Upload Photo
          </button>
          <input
            type="file"
            accept="image/*"
            onChange={capturePhoto}
            ref={fileInputRef}
            className="hidden"
          />
          <p className="text-sm text-gray-600 text-center">
            Take or upload a photo of your food to get started!
          </p>
        </div>
      )}

      {capturedImage && !results && (
        <div className="space-y-4">
          <img
            src={capturedImage}
            alt="Captured food"
            className="w-full rounded-lg"
          />
          <div className="flex space-x-2">
            <button
              onClick={resetCapture}
              className="flex-1 bg-red-500 text-white p-2 rounded-lg flex items-center justify-center"
            >
              <X className="mr-2" />
              Retake
            </button>
            <button
              onClick={processPhoto}
              className="flex-1 bg-green-500 text-white p-2 rounded-lg flex items-center justify-center"
            >
              <Check className="mr-2" />
              Analyze
            </button>
          </div>
        </div>
      )}

      {isProcessing && (
        <div className="flex flex-col items-center justify-center py-8">
          <Loader className="animate-spin text-blue-500 mb-4" size={48} />
          <p className="text-lg font-semibold">Analyzing your food...</p>
        </div>
      )}

      {results && (
        <div className="space-y-4">
          <h3 className="text-xl font-semibold">Analysis Results</h3>
          <div className="bg-white p-4 rounded-lg shadow space-y-4">
            <div>
              <h4 className="font-semibold mb-2">Detected Ingredients:</h4>
              {isEditing ? (
                <ul className="space-y-2">
                  {editedResults?.ingredients.map((ingredient, index) => (
                    <li key={index} className="flex items-center">
                      <input
                        type="text"
                        value={ingredient}
                        onChange={(e) =>
                          handleIngredientChange(index, e.target.value)
                        }
                        className="flex-grow border rounded px-2 py-1 mr-2"
                      />
                      <button
                        onClick={() => handleRemoveIngredient(index)}
                        className="text-red-500"
                      >
                        <MinusCircle size={20} />
                      </button>
                    </li>
                  ))}
                  <li>
                    <button
                      onClick={handleAddIngredient}
                      className="text-blue-500 flex items-center"
                    >
                      <PlusCircle size={20} className="mr-1" /> Add Ingredient
                    </button>
                  </li>
                </ul>
              ) : (
                <ul className="list-disc pl-5">
                  {results.ingredients.map((ingredient, index) => (
                    <li key={index}>{ingredient}</li>
                  ))}
                </ul>
              )}
              {!isEditing && (
                <p className="mt-2">
                  Confidence: {(results.confidence * 100).toFixed(1)}%
                </p>
              )}
            </div>

            <div>
              <h4 className="font-semibold mb-2">Nutritional Information:</h4>
              {isEditing ? (
                <input
                  type="text"
                  value={editedResults?.nutrition.servingSize}
                  onChange={(e) =>
                    handleNutritionChange('servingSize', e.target.value)
                  }
                  className="w-full border rounded px-2 py-1 mb-2"
                />
              ) : (
                <p className="text-sm text-gray-600 mb-2">
                  Serving Size: {results.nutrition.servingSize}
                </p>
              )}
              <NutritionItem
                label="Calories"
                value={
                  isEditing
                    ? editedResults?.nutrition.calories ?? 0
                    : results.nutrition.calories
                }
                unit="kcal"
                editable={isEditing}
                onChange={(value) => handleNutritionChange('calories', value)}
              />
              <NutritionItem
                label="Carbohydrates"
                value={
                  isEditing
                    ? editedResults?.nutrition.carbohydrates ?? 0
                    : results.nutrition.carbohydrates
                }
                unit="g"
                editable={isEditing}
                onChange={(value) =>
                  handleNutritionChange('carbohydrates', value)
                }
              />
              <NutritionItem
                label="Fat"
                value={
                  isEditing
                    ? editedResults?.nutrition.fat ?? 0
                    : results.nutrition.fat
                }
                unit="g"
                editable={isEditing}
                onChange={(value) => handleNutritionChange('fat', value)}
              />
              <NutritionItem
                label="Protein"
                value={
                  isEditing
                    ? editedResults?.nutrition.protein ?? 0 // Provide a default value
                    : results.nutrition.protein
                }
                unit="g"
                editable={isEditing}
                onChange={(value) => handleNutritionChange('protein', value)}
              />
            </div>
          </div>

          {feedback === null ? (
            <div className="flex space-x-2">
              <button
                onClick={() => handleFeedback(true)}
                className="flex-1 bg-green-500 text-white p-3 rounded-lg flex items-center justify-center"
              >
                <ThumbsUp className="mr-2" />
                Confirm Results
              </button>
              <button
                onClick={() => handleFeedback(false)}
                className="flex-1 bg-red-500 text-white p-3 rounded-lg flex items-center justify-center"
              >
                <ThumbsDown className="mr-2" />
                Edit Results
              </button>
            </div>
          ) : isEditing ? (
            <button
              onClick={handleSaveEdits}
              className="w-full bg-blue-500 text-white p-3 rounded-lg flex items-center justify-center"
            >
              <Save className="mr-2" />
              Save Edits
            </button>
          ) : (
            <div className="text-center p-3 bg-blue-100 text-blue-800 rounded-lg">
              Thank you for your feedback!
            </div>
          )}

          <button
            onClick={resetCapture}
            className="w-full bg-blue-500 text-white p-3 rounded-lg"
          >
            Analyze Another Photo
          </button>
        </div>
      )}
    </div>
  );
};

export default PhotoCaptureComponent;
