import React, { useState, ChangeEvent, FormEvent } from "react";
import axios from "axios";
import './ImageUpload.css';

interface UploadResponse {
  ingredients: string;
}

const ImageUpload: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [ingredients, setIngredients] = useState<string[] | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files ? e.target.files[0] : null;
    setSelectedFile(file);
    setIngredients(null); // Reset ingredients when a new file is selected
    setError(null); // Reset error when a new file is selected
  };

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    if (!selectedFile) {
      alert("Please upload an image first.");
      return;
    }

    setLoading(true);
    setError(null);

    // Prepare the form data
    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      // Send the image to the backend API
      const response = await axios.post<UploadResponse>("http://localhost:8000/upload-image/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      // Extract the ingredients from the response
      const { ingredients } = response.data;
      setIngredients(ingredients);
    } catch (error) {
      setError("Failed to fetch ingredients. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="image-upload-container">
      <h2>Upload an Image to Get Ingredients</h2>
      <form onSubmit={handleSubmit} className="image-upload-form">
        <input
          type="file"
          accept="image/*"
          onChange={handleFileChange}
        />
        <button type="submit" disabled={loading}>
          {loading ? "Uploading..." : "Upload and Analyze"}
        </button>
      </form>

      {/* Display loading status */}
      {loading && <div className="loading">Processing image...</div>}

      {/* Display error if something went wrong */}
      {error && <div className="error">{error}</div>}

      {/* Display ingredients when available */}
      {ingredients && (
        <div className="ingredients-list">
          <h3>Ingredients Found:</h3>
          <ul>
            {ingredients.split("\n").map((ingredient, index) => (
              <li key={index}>{ingredient}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ImageUpload;
